
class Elevator
  def initialize(instructions)
    @instructions = instructions.chars
  end

  def final_floor
    each_floor do
      travel
    end

    @elevator
  end

  def first_negative
    step = 0
    each_floor do
      step += 1
      travel
      return step if @elevator < 0
    end
  end

  def travel
    if up?
      @elevator += 1
    elsif down?
      @elevator -= 1
    end
  end

  def up?
    @floor == '('
  end

  def down?
    @floor == ')'
  end

  def each_floor
    @elevator = 0
    @instructions.each do |instruction|
      @floor = instruction
      yield(@floor)
    end
  end
end

