
class Keypad
  KEYS = [
    [1, 2, 3],
    [4, 5, 6],
    [7, 8, 9]
  ].reverse

  def initialize(directions)
    @directions = directions
  end

  def code
    reset
    @directions.map do |line|
      follow(line)
      KEYS[@location[0]][@location[1]]
    end
  end

  def follow(path)
    path.strip.chars.each do |direction|
      case direction
      when 'U'
        up
      when 'D'
        down
      when 'L'
        left
      when 'R'
        right
      end
    end
  end

  def reset
    @location = [1, 1]
  end

  def up
    @location[0] = [@location[0] + 1, 2].min
  end

  def down
    @location[0] = [@location[0] - 1, 0].max
  end

  def left
    @location[1] = [@location[1] - 1, 0].max
  end

  def right
    @location[1] = [@location[1] + 1, 2].min
  end
end

